var EventEmitter = require('events').EventEmitter;

function Shader (code, filename) {
  EventEmitter.call(this);
  this.filename = filename || '';
  this.code = code || '';
  this.version = 0;
}

Shader.prototype = Object.create(EventEmitter.prototype, {
  constructor: {
    value: Shader,
    enumerable: false,
    writable: true,
    configurable: true
  }
});

module.exports = Shader;
