const path = require('path');

const glslExtensions = [
  '.glsl',
  '.vert',
  '.frag',
  '.geom',
  '.vs',
  '.fs',
  '.gs',
  '.vsh',
  '.fsh',
  '.gsh',
  '.vshader',
  '.fshader',
  '.gshader'
];

module.exports.list = [ '.js', '.json', '.css', '.sass', '.scss', '.less' ].concat(glslExtensions);

module.exports.glslExtensions = glslExtensions;

module.exports.isCSS = file => /\.(css|less|sass|scss)$/i.test(file);

module.exports.isGLSL = file => {
  file = file.toLowerCase();
  if (glslExtensions.includes(file)) return true;
  const ext = path.extname(file);
  return glslExtensions.includes(ext);
};
