module.exports = function bundleError (name) {
  console.warn([
    'It looks like you have required "' + name + '" in one ',
    'of your scripts, except you are not using texel to ',
    'run and bundle your code. The texel development tools rely ',
    'on texel and its development server to work correctly, ',
    'for more details see here:\n  https://www.npmjs.com/package/texel',
    '\nIf you are requiring a module from node_modules, it must be transpiled',
    'to plain ES5 beforehand.'
  ].join(''));
};
